(function($){
  jQuery.fn.trollSlider = function(options){
        var start = function(){
                trollSlider.init($(this));
                trollSlider.horizontScroll($(this));

                $('#next-slide').on('click', function(){
                    trollSlider.nextSlide();
                });
                $('#prev-slide').on('click', function(){
                    trollSlider.prevSlide();
                });
                $('#dots button').on('click', function(){
                    trollSlider.clickDot($(this));
                });
                $(this).on('click', '.one-slide[data-slide=2]', function(){
                    trollSlider.nextSlide();
                });
                $(this).on('click', '.one-slide[data-slide=3]', function(){
                    trollSlider.nextSlide();
                });
                $(this).find('img').attr({onselectstart:"return false", ondrag:"return false", ondragdrop:"return false", ondragstart:"return false" });

        }
        var trollSlider = {
            settings : $.extend({
                showItems : 3,
                dots:true,
                arrows:true,
                triger:false,
                animateDuration: '500ms',
                dotsSlider:false,
                initialization:false,
                maxIndexOfDots : 0
            },options),
             actualDot: function(){
                if(trollSlider.settings.dotsSlider==false){
                    $('#dots button').removeClass('actual');
                    var i = $('.active-slide').attr('data-dot');
                    $('#dots button[data-dot='+i+']').addClass('actual');
                }else{
                    $('#dots button').removeClass('actual');
                    $('#dots li').eq(2).find('button').addClass('actual');
                }
            },
            indexSlide: function(){
                var slider = $('.troll-slider');
                $('.one-slide').removeClass('second active-slide third unactive-slide');
                slider.find('.one-slide[data-slide=1]').addClass('active-slide');
                slider.find('.active-slide').next().addClass('second');
                slider.find('.second').next().addClass('third');
                $('.one-slide').each(function() {
                    var i = $(this).attr('data-slide');
                    if(i>trollSlider.settings.showItems || i<=0 ){$(this).addClass('unactive-slide');}
                });
            },
            init : function(slider){
                if(trollSlider.settings.initialization == false){
                    var i = 1;
                    var numItems = $('.one-slide').length+1;

                    var dots ='';
                    if (!$('.one-slide').attr('data-slide')){
                        $('.one-slide').each(function() {
                            $(this).attr({ 'data-slide': i, 'data-dot': i });
                            var imgLink= $(this).find('.image').find('img').attr('src');
                            dots = dots+'<li><button data-dot="'+i+'" style="background-image:url(./'+imgLink+')"></button></li>';
                            i++;
                        });
                    };
                    if ($('.one-slide[data-slide=0]').length==0){
                        var slide = $('.one-slide:last-child');
                         $('.one-slide:last-child').remove();
                         slide.attr('data-slide', 0 );
                         $('.troll-slider').prepend(slide);
                     };
                    trollSlider.indexSlide();
                    if($('#arrows').length==0 && trollSlider.settings.arrows == true){slider.parent().append('<div id="arrows"><button id="prev-slide" class="prev-slide slider-arrows"></button><button id="next-slide" class="next-slide slider-arrows"></button></div>')};
                    if($('#dots').length==0 && trollSlider.settings.dots == true){slider.parent().append('<ul id="dots">'+dots+'</ul>')};
                    //dots reworking


                        $('#dots li').each(function() {
                            if($(this).index()>trollSlider.settings.maxIndexOfDots){trollSlider.settings.maxIndexOfDots = $(this).index();}
                        });

                        if (trollSlider.settings.maxIndexOfDots>5){
                            trollSlider.settings.dotsSlider = true;
                            $('#dots').addClass('many-dots');
                        }


                    trollSlider.dotsSlider();
                    trollSlider.actualDot();
                    trollSlider.settings.initialization = true;
                }
            },
            dotsSlider:function(){
                if(trollSlider.settings.dotsSlider == true){
                    console.log('start dotsSlider');
                    var sumOfCloned = 0;
                    $('#dots li').each(function() {
                         if($(this).data('cloned') == "true"){sumOfCloned++;}
                    });
                    console.log('cloned dots'+sumOfCloned);
                    if(sumOfCloned<2){
                        $('#dots li').eq(trollSlider.settings.maxIndexOfDots).data('cloned', 'true').prependTo('#dots');
                    }
                    if(sumOfCloned<2){
                        $('#dots li').eq(trollSlider.settings.maxIndexOfDots).data('cloned', 'true').prependTo('#dots');
                    }
                }
            },
            clickDot: function(object){
                var define = object.attr('data-dot') - $('.active-slide').attr('data-dot');
                if (define<0){for (var i = 0; i<Math.abs(define); i++) {trollSlider.prevSlide();}};
                if(define>0){for (var i = 0; i<define; i++) {trollSlider.nextSlide();}};
            },
            nextSlide: function (){
                    if(trollSlider.settings.triger==false){
                        trollSlider.settings.triger = true;
                        $('.active-slide .image').attr("style", "animation : animateActiveSlideNext "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateActiveSlideNext "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateActiveSlideNext "+trollSlider.settings.animateDuration+" linear; -o-animation : animateActiveSlideNext "+trollSlider.settings.animateDuration+" linear;");
                        $('.second .image').attr("style", "animation : animateSecondSlideNext "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateSecondSlideNext "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateSecondSlideNext "+trollSlider.settings.animateDuration+" linear; -o-animation : animateSecondSlideNext "+trollSlider.settings.animateDuration+" linear;");
                        $('.third .image').attr("style", "animation : animateThirdSlideNext "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateThirdSlideNext "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateThirdSlideNext "+trollSlider.settings.animateDuration+" linear; -o-animation : animateThirdSlideNext "+trollSlider.settings.animateDuration+" linear;");
                        $('.one-slide[data-slide="4"] .image').attr("style", "animation : animateFourSlideNext "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateFourSlideNext "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateFourSlideNext "+trollSlider.settings.animateDuration+" linear; -o-");
                        $('.one-slide[data-slide="0"] .image').removeAttr("style");
                         $('.one-slide').each(function() {
                            var x = $(this).attr('data-slide');
                            x--;
                            $(this).attr({ 'data-slide': x });
                        });
                         var numItems = $('.one-slide').length-1;
                         var slide = $('.one-slide[data-slide=-1]');
                         $('.one-slide[data-slide=-1]').remove();
                         slide.attr('data-slide', numItems );
                         $('.troll-slider').append(slide);
                         $('#dots li').eq(0).appendTo('#dots');
                         trollSlider.indexSlide();
                         trollSlider.actualDot();
                         trollSlider.settings.triger=false;

                     }
            },
             prevSlide: function(){
                 if(trollSlider.settings.triger==false){
                        trollSlider.settings.triger = true;
                        $('.active-slide .image').attr("style", "animation : animateActiveSlidePrev "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateActiveSlidePrev "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateActiveSlidePrev "+trollSlider.settings.animateDuration+" linear; -o-animation : animateActiveSlidePrev "+trollSlider.settings.animateDuration+" linear;");
                        $('.second .image').attr("style", "animation : animateSecondSlidePrev "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateSecondSlidePrev "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateSecondSlidePrev "+trollSlider.settings.animateDuration+" linear; -o-animation : animateSecondSlidePrev "+trollSlider.settings.animateDuration+" linear;");
                        $('.third .image').attr("style", "animation : animateThirdSlidePrev "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateThirdSlidePrev "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateThirdSlidePrev "+trollSlider.settings.animateDuration+" linear; -o-animation : animateThirdSlidePrev "+trollSlider.settings.animateDuration+" linear;");
                        $('.one-slide[data-slide="0"] .image').attr("style", "animation : animateZeroSlidePrev "+trollSlider.settings.animateDuration+" linear; -webkit-animation : animateZeroSlidePrev "+trollSlider.settings.animateDuration+" linear; -moz-animation : animateZeroSlidePrev "+trollSlider.settings.animateDuration+" linear; -o-animation : animateZeroSlidePrev "+trollSlider.settings.animateDuration+" linear;");
                        $('.one-slide[data-slide="4"] .image').removeAttr("style");
                         $('.one-slide').each(function() {
                            var x = $(this).attr('data-slide');
                            x++;
                            $(this).attr({ 'data-slide': x });
                        });
                         var numItems = $('.one-slide').length;
                         var slide = $('.one-slide:last-child');
                         $('.one-slide:last-child').remove();
                         slide.attr('data-slide', 0 );
                         $('.troll-slider').prepend(slide);
                         $('#dots li').eq(trollSlider.settings.maxIndexOfDots).prependTo('#dots');
                         trollSlider.indexSlide();
                         trollSlider.actualDot();
                         trollSlider.settings.triger=false;
                     }
            },
            horizontScroll:function(slider){
                var flag =true;
                var cX;
                slider.on('mousedown', (function(evt) {
                        flag = false;
                        cX = evt.clientX;


                    slider.on('mousemove',(function(evt) {
                        if(flag == false){
                            if((cX-evt.clientX)>50){trollSlider.nextSlide(); flag=true;}
                            if((cX-evt.clientX)<-50){trollSlider.prevSlide(); flag=true;}
                             console.log((cX-evt.clientX));
                         }
                    }));
                }));
                slider.on('mouseup',function(){ flag=true;});
            }
        }
        return this.each(start);
    };
})(jQuery);